import React, { useState, useEffect, useCallback } from "react";
import {Container,Form,Input,Textarea,Button,H3} from "./styles";
function Cadastro(){


    const initialValue={
        sku:"",
        name:"",
        description:"",
        price:"",
        quantity:"",
        image:""
    };
    const [messageError, setMessageError] = useState(null);
    const [product,setProduct] =useState(initialValue);

    const handleChange= useCallback(
        (e)=>{
        setProduct({
            ...product,
            [e.target.name]:e.target.value,
        });
    },
    [product]
    );


    const handleSubmit= useCallback((e)=>{
        e.preventDefault();
        console.log(e)
    }

    )
    function validate() {
        if(product.sku == "") {
          setMessageError('sku is empty!')
        }else {
          setMessageError(null)
        }
      }

    return (
    <Container>
        

        <Form onSubmit={handleSubmit}>
        <H3>cadastro</H3>
        <Input  onBlur={validate} placeholder="SKU" onChange={handleChange}  name="sku" value={product.sku}/>
        <Input onBlur={validate}  placeholder="nome"  onChange={handleChange} name="name" value={product.name}/>
        <Textarea onBlur={validate}  placeholder="description" onChange={handleChange}  name="description" value={product.description}></Textarea>
        <Input onBlur={validate}  type="number" placeholder="preÃ§o" onChange={handleChange}  name="price" value={product.price}/>
        <Input onBlur={validate}  type="number"  placeholder="quantity" onChange={handleChange}  name="quantity" value={product.quantity}/>
        <Input onBlur={validate}  placeholder="image" onChange={handleChange} name="image" value={product.image}/>
        <Button>Cadastrar</Button>
        {messageError && <div className="row">
          <strong>{messageError}</strong>
        </div>}
        </Form>
    </Container>
    );
}
export default Cadastro;
