import React, {
    useEffect,
    useState,
  } from "react";

function List(){


const[produtos,SetProdutos]=useState([])
const [currentProduto, setCurrentProduto] = useState(null);
console.log("test");
useEffect(()=>{
const prods=localStorage.getItem("produtos");
console.log(prods);
SetProdutos(prods);

},[])
return  (
    <div>
      <h3>Listagem de usuários</h3>
      <h1>Quantidade de usuários {produtos.length}</h1>

      <table>
        <thead>
          <tr>
            <th>_id</th>
            <th>Email</th>
            <th>Name</th>
            <th>Username</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {produtos.map((user) => (
            <tr key={user._id}>
              <td>{user._id}</td>
              <td>{user.email}</td>
              <td>{user.name}</td>
              <td>{user.username}</td>
              <td>
                <button onClick={() => setCurrentProduto(user)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

    
    </div>
  );


}
export default List;
