import styled from "styled-components";

export const Container=styled.div`
background:#ddd;
width:50%;
display:flex;
padding:40px;
padding-top:0px;
margin-left:25%;
`;

export const Form = styled.form`

`;
export const H3 = styled.h3`
text-align:center;
font-size:35px;
`;

export const Input = styled.input`
padding:10px;
width:97%;
`;

export const Textarea = styled.textarea`
padding:10px;
border-radius:5px;
margin-top:10px;
border:1px solid #b8b8b8;
outline:none;
width:97%;
:focus {
    border:1px solid #90a8de;
    outline:none;
}`;

export const Button= styled.button`
height:40px;
outline:none;
background:#a50000;
border-radius:5px;
color:white;
margin-top:1px solid #888;
width:100%;
:active{
    backgroud:#fff;
}

`;
