import styled from "styled-components";

export const Container=styled.div`
background:#fffaaa;
width:500px;
display:flex;
`;

export const Form = styled.form`
max-width:100%
`;

export const Input = styled.input`
padding:10px;
`;

export const Textarea = styled.Textarea`
padding:10px;
`;
