import React, {
    useEffect,
    useState,
    useCallback,
  } from "react";
  import Editar from "../Editar";
  import "./style.css";
function List(){

    const loadProdutos=useCallback(() => {
        const prods=JSON.parse(localStorage.getItem("produtos")) ;
        console.log(prods);
        SetProdutos(prods);
          
      }, []);
    
      const callBackEdit = (dataFromChild) => {
        const { tipoAcao } = dataFromChild;
        console.log("callBack "+tipoAcao);
        if (tipoAcao === "sucessoEdit") {
            SetProdutos();
          setCurrentProduto(null);
        } else if (tipoAcao === "closeModal") {
            setCurrentProduto(null);
        }
      };
const[produtos,SetProdutos]=useState([])
const [currentProduto, setCurrentProduto] = useState(null);

useEffect(()=>{
console.log(prods);
SetProdutos(prods);


},[])
return  (
    <div>
      
      <h1>Total Products:  {produtos.length}</h1>

      <table>
        <thead>
          <tr>
            <th>SKU</th>
            <th>Nome</th>
            <th>Pre√ßo</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {produtos.map((produto) => (
            <tr key={produto.sku}>
              <td>{produto.sku}</td>
              <td>{produto.name}</td>
              <td>{produto.price}</td>
              <td>
                <button onClick={() => setCurrentProduto(produto)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {currentProduto && <Editar product={currentProduto} callBack={callBackEdit} />}
    </div>
  );


}
export default List;
