import styled from "styled-components";

export const Container=styled.div`
background:#fffaaa;
width:500px;
display:flex;
`;

export const Form = styled.form`
max-width:100%
`;

export const Input = styled.input`
padding:10px;
`;

export const Textarea = styled.textarea`
padding:10px;
border-radius:5px;
margin-top:10px;
border:1px solid #b8b8b8;
outline:none;
:ficus {
    border:1px solid #90a8de;
    outline:none;
}
`;

export const Button= styled.button`
height:40px;
outline:none;
background:#999;
border-radius:5px;
margin-top:1px solid #888;
width:100%;
:hover{
    backgroud:#aba8a8;
}

`;
